mapper.select:
select a.* ,b.* from heroland_competition_record a left join heroland_question_record_detail b
on a.record_id = b.record_id
where a.is_deleted = 0 and b.is_deleted = 0


    and a.topic = #{topic,jdbcType=VARCHAR}



    and a.record_id = #{recordId,jdbcType=VARCHAR}



    limit #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(a.*) from heroland_competition_record a left join heroland_question_record_detail b
on a.record_id = b.record_id
where a.is_deleted = 0 and b.is_deleted = 0


    and a.topic = #{topic,jdbcType=VARCHAR}



    and a.record_id = #{recordId,jdbcType=VARCHAR}

select sum(total_score) total_score,user_id ,type,grade_code,class_code ,org_code,subject_code sum(total_score)/sum(competitionCount) avgScore,sum(competitionCount) total_games from ( select sum(invite_score) total_score,invite_id user_id ,topic_type type ,class_code class_code,grade_code grade_code,org_code org_code,subject_code ,count(invite_score) competitionCount from heroland_competition_record where is_deleted = 0
and topic_type = #{type}
GROUP BY org_code,topic_type,grade_code,class_code,subject_code ,invite_id union all
select sum(opponent_score) total_score , opponent_id user_id ,topic_type type ,class_code class_code,grade_code grade_code,org_code org_code,subject_code count(opponent_score) competitionCount from heroland_competition_record where is_deleted = 0
and topic_type = #{type}
GROUP BY org_code,topic_type,grade_code,class_code,subject_code ,opponent_id
) a GROUP BY user_id

select * from heroland_competition_record where is_deleted = 0 and topic_id in
#{topicId}
and (invite_id = #{userId} or opponent_id = #{userId})

