mapper.select:
SELECT


CASE
WHEN @prevRank = ${orderField} THEN @curRank
WHEN @prevRank := ${orderField} THEN @curRank := @curRank + 1
END AS rank
FROM heroland_statistics_detail p,
(SELECT @curRank :=0, @prevRank := NULL) r

where is_deleted =0


  and  user_id = #{userId}



    and subject_code = #{subjectCode,jdbcType=VARCHAR}


ORDER BY ${orderField}



    limit #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT
    count(*)
FROM heroland_statistics_detail

where is_deleted =0


    and  user_id = #{userId}



    and subject_code = #{subjectCode,jdbcType=VARCHAR}
