mapper.update:
update heroland_question_bank
set is_deleted = 1
where 1 = 1

    and qt_id IN (

        #{qtId,jdbcType=VARCHAR}

    )

mapper.select:
select

from heroland_question_bank
where is_deleted = 0

    and grade_code = #{item.grade,jdbcType=VARCHAR}


    and course = #{item.course,jdbcType=VARCHAR}


    and `type` = #{item.type,jdbcType=INTEGER}


    and sub_type = #{item.subType,jdbcType=INTEGER}


    and diff = #{item.diff,jdbcType=INTEGER}


    and `year` >= #{item.beginTime,jdbcType=TIMESTAMP}



and `year` <= #{item.endTime,jdbcType=TIMESTAMP}




    and area = #{item.area,jdbcType=VARCHAR}


    and `source` = #{item.source,jdbcType=VARCHAR}


    and paper_type = #{item.paperType,jdbcType=INTEGER}


    and bank_type = #{item.bankType,jdbcType=INTEGER}


    and title like concat('%',#{item.title},'%')


    and id IN (

        #{id,jdbcType=BIGINT}

    )

select

from heroland_question_bank
where is_deleted = 0

    and grade_code = #{item.grade,jdbcType=VARCHAR}


    and course = #{item.course,jdbcType=VARCHAR}


    and type IN (

        #{type,jdbcType=INTEGER}

    )


    and diff IN (

        #{diff,jdbcType=INTEGER}

    )



    and `year` >= #{item.beginTime,jdbcType=TIMESTAMP}



and `year` <= #{item.endTime,jdbcType=TIMESTAMP}




    and area IN (

        #{area,jdbcType=VARCHAR}

    )


    and `source` IN (

        #{source,jdbcType=VARCHAR}

    )



    and paper_type IN (

        #{paperType,jdbcType=INTEGER}

    )



    and bank_type = #{item.bankType,jdbcType=INTEGER}


    and (bank_type = 1 or bank_type = 2)



    and id IN (

        #{id,jdbcType=BIGINT}

    )

select

from heroland_question_bank


        and id IN (

            #{id,jdbcType=BIGINT}

        )

    and is_deleted = 0

select

from heroland_question_bank


        and id IN (

            #{id,jdbcType=BIGINT}

        )

select

from heroland_question_bank


        and qt_id IN (

            #{qtId,jdbcType=VARCHAR}

        )

    and is_deleted = 0

select htq.id, htq.question_id as questionId, hqb.snapshot_no as snapshotNo, hqb.diff as diff, hqb.paper_type as paperType, hqb.course as course, hqb.type as type, hqb.grade_code as gradeCode, htg.level_code as topicLevelCode, htg.topic_name as topicName, htg.type as topicType, htg.diff as topicDiff, group_concat(a.knowledge) as knowledges, htq.topic_id as topicId, htg.start_time as startTime, htg.end_time as endTime from heroland_topic_question htq left join heroland_topic_group htg on htg.id = htq.topic_id left join heroland_question_bank hqb on htq.question_id = hqb.id left join (select hkr.refer_id, hk.knowledge from heroland_knowledge_refer hkr left join heroland_knowledge hk on hk.id = hkr.knowledge_id where hkr.type = 2) as a on hqb.id = a.refer_id where htg.is_deleted = 0 and htq.is_deleted = 0 and (hqb.bank_type = 1 or hqb.bank_type = 2)
and htg.org_code = #{item.orgCode,jdbcType=VARCHAR}
and htg.grade_code = #{item.gradeCode,jdbcType=VARCHAR}
and htg.class_code = #{item.classCode,jdbcType=VARCHAR}
and htg.course_code = #{item.courseCode,jdbcType=VARCHAR}
and htg.`type` = #{item.type,jdbcType=INTEGER}
and htg.`gmt_create` >= #{item.startTime,jdbcType=TIMESTAMP}
and htg.`gmt_create` <= #{item.endTime,jdbcType=TIMESTAMP}
group by htq.id

select htq.id, htq.question_id as questionId, hqb.snapshot_no as snapshotNo, hqb.diff as diff, hqb.paper_type as paperType, hqb.course as course, hqb.type as type, hqb.grade_code as gradeCode, htg.level_code as topicLevelCode, htg.topic_name as topicName, htg.type as topicType, htg.diff as topicDiff, group_concat(a.knowledge) as knowledges, htq.topic_id as topicId, htg.start_time as startTime, htg.end_time as endTime from heroland_topic_question htq left join heroland_topic_group htg on htg.id = htq.topic_id left join heroland_question_bank hqb on htq.question_id = hqb.id left join (select hkr.refer_id, hk.knowledge from heroland_knowledge_refer hkr left join heroland_knowledge hk on hk.id = hkr.knowledge_id where hkr.type = 2) as a on hqb.id = a.refer_id where htg.is_deleted = 0 and htq.is_deleted = 0 and (hqb.bank_type = 1 or hqb.bank_type = 2)
and htg.org_code = #{item.orgCode,jdbcType=VARCHAR}
and htg.grade_code = #{item.gradeCode,jdbcType=VARCHAR}
and htg.class_code = #{item.classCode,jdbcType=VARCHAR}
and htg.course_code = #{item.courseCode,jdbcType=VARCHAR}
and htg.`type` = #{item.type,jdbcType=INTEGER}
and htg.`gmt_create` >= #{item.startTime,jdbcType=TIMESTAMP}
and htg.`gmt_create` <= #{item.endTime,jdbcType=TIMESTAMP}
group by htq.topic_id

select

from heroland_question_bank


        and grade_code = #{grade,jdbcType=VARCHAR}


        and course = #{course,jdbcType=VARCHAR}


        and bank_type = #{bankType,jdbcType=INTEGER}


        and id > #{lastId,jdbcType=BIGINT}

    and is_deleted = 0


    limit #{count,jdbcType=INTEGER}

select max(id) from heroland_question_bank


            and grade_code = #{grade,jdbcType=VARCHAR}


            and course = #{course,jdbcType=VARCHAR}


            and bank_type = #{bankType,jdbcType=INTEGER}

        and is_deleted = 0
