mapper.select:
select

from heroland_question_bank
where is_deleted = 0

 and grade_code = #{grade,jdbcType=VARCHAR}


 and course = #{course,jdbcType=VARCHAR}


 and `type` = #{type,jdbcType=INTEGER}


 and sub_type = #{subType,jdbcType=INTEGER}


 and diff = #{diff,jdbcType=INTEGER}


 and `year` = #{year,jdbcType=VARCHAR}


 and area = #{area,jdbcType=VARCHAR}


 and `source` = #{source,jdbcType=VARCHAR}


 and paper_tpye = #{paperTpye,jdbcType=INTEGER}


  and title like concat('%',#{title},'%')
