mapper.update:
update heroland_topic_question
set is_deleted = 1


    and topic_id IN (

      #{topicId,jdbcType=BIGINT}

    )

mapper.select:
select

from heroland_topic_question


    and topic_id IN (

      #{topicId,jdbcType=BIGINT}

    )


    and question_id = #{questionId,jdbcType=BIGINT}

  and is_deleted = 0
